<!-- // fichier de gestion du profiltraitements--><?php	include ('modeles/profil.mo.php');				$actions = array(1,2,3,4,5,6); // Tableau des actions possibles	/*		Action 1 : modifier pseudo		Action 2 : modifier mot de passe		Action 3 : modifier email		Action 4 : Traitement modification pseudo		Action 5 : Traitement modification mot de passe		Action 6 : traitement modification email		Default : Affichage du profil	*/	if(isset($_GET['action']) && in_array($_GET['action'],$actions))		$action = securite($_GET['action']);	else		$action = 0; // page par defaut		$sousPage 	= "defaut";			// traitement pour chaque type de page et calcul de la page Ã  afficher	switch ($action)	{ 		case 1:			$sousPage 	= "pseudo";		break;				case 2:			$sousPage 	= "mdp";		break;				case 3:			$sousPage 	= "email";		break;				case 4:			// calcul de la sous-page			if (isset($_POST['pseudo'])) {				MAJPseudo(securite($_POST['pseudo']));								$sousPage 	= "defaut";			} else {				header("Location: index.php?page=profil&message=erreurFormulaire");			}					break;				case 5:			if (isset($_POST['ancien']) && isset($_POST['mdp']) && isset($_POST['mdp2'])) {				MAJMotDePasse(sha1(securite($_POST['ancien'])),sha1(securite($_POST['mdp'])),sha1(securite($_POST['mdp2'])));			} else {				header("Location: index.php?page=profil&message=erreurFormulaire");			}					break;				case 6:			if (isset($_POST['mail']) && isset($_POST['mail2'])) {				MAJMail(securite($_POST['mail']),securite($_POST['mail2']));			} else {				header("Location: index.php?page=profil&message=erreurFormulaire");			}				break;				default :			$sousPage 	= "defaut";	}				include ('vues/profil/profil_'.$sousPage.'.vu.php');?>